#Part 2
def find_first_basement_position(instructions):
    floor = 0
    position = 0

    for instruction in instructions:
        position += 1

        if instruction == '(':
            floor += 1
        elif instruction == ')':
            floor -= 1

        if floor == -1:
            return position, instruction

    return -1, None  

